import { tags } from "typia";

/**
 * Standard type for confirming single-object deletion in API results. Used for
 * soft/hard deletes in audit/logging flows.
 *
 * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
 */
export type IDeleteResult = {
  /**
   * Operation result code or human-readable status description. Example
   * values: 'success', 'not_found', 'already_deleted'. Should provide
   * machine-auditable result context.
   */
  status: string;

  /**
   * The unique identifier of the object that was deleted, matching primary
   * key requirement in referenced table.
   */
  deleted_id: string & tags.Format<"uuid">;
};
